import React, { Component } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class AddMyuser extends Component {

  constructor(props) {
    super(props)

    // Setting up functions
    this.onChangeUserFName = this.onChangeUserFName.bind(this);
    this.onChangeUserEmail = this.onChangeUserEmail.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      fname: '',
      lname:'',
      date:'',

      email: '',
      password:'',
      somdac:'',
      touched: {
        fname: false,
        lname: false,
        date: false,
        email: false,
        password: false,
        somdac: false,
    }
    }
  }

  onChangeUserFName(e) {
    this.setState({ firstname: e.target.value })
  }
  onChangeUserEmail(e) {
    this.setState({ lname: e.target.value })
  }






  onSubmit(e) {
    e.preventDefault()

    const userObject = {
      firstname: this.state.fname,
      lname: this.state.lname,
      email: this.state.email,
      date:this.state.date,
      password:this.state.password,
      somdac:this.state.somdac
    };


    axios.post('http://localhost:4000/users/add-user', userObject)
      .then(res => console.log(res.data));


    this.setState({ fname: '', lname:'',email: '', date:'',password:'',somdac:'' })
  }

  render() {
    return (<div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="FName">
          <Form.Label>First Name</Form.Label>
          <input type="text" value={this.state.fname} onChange={this.onChangeUserFName } />
        </Form.Group>
        <Form.Group controlId="LName">
          <Form.Label>Last Name</Form.Label>
          <Form.Control type="text" value={this.state.lname} onChange={this.onChangeUserName} />
        </Form.Group>
        <Form.Group controlId="DOB">
          <Form.Label>Date of Birth</Form.Label>
          <Form.Control type="date" value={this.state.date} onChange={this.onChangeUserName} />
        </Form.Group>
        <Form.Group controlId="Email">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" value={this.state.email} onChange={this.onChangeUserName} />
        </Form.Group>

        <Form.Group controlId="Password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" value={this.state.password} onChange={this.onChangeUserName} />
        </Form.Group>
        <Form.Group controlId="Somdac">
          <Form.Label>Social Media Account</Form.Label>
          <Form.Control type="textarea" value={this.state.somdac} onChange={this.onChangeUserName} />
        </Form.Group>


        <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
          Create Student
        </Button>
      </Form>
    </div>);
  }
}
