import React, { Component } from 'react';

import {Form ,Button} from 'react-bootstrap/Form';


import { useState } from 'react';
import { FormGroup ,col} from 'reactstrap';

  function AddUser({ addChoreLog }) {

    const [fname, setfName] = useState();
    const [lname, setlName] = useState();
    const [email, setemail] = useState();
    const [pswd, setpswd] = useState();
    const [SoMdAc,setSoMdAc] = useState();
    const [img, setimg] = useState();

    const [date, setdate] = useState();
    const handleSubmit= (e) => {
        AddUser([fname,lname,email,pswd,setSoMdAc,date])
      e.preventDefault();
    }

    return (
        <div className='container'>
      <form onSubmit={e => { handleSubmit(e) }}>

          <FormGroup>
        <label>First Name :</label>

        <input 
          name='fname' 
          type='text'
          value={fname}
         // required
          onChange={e => setfName(e.target.value)}
        /></FormGroup>

    <col md={10}>
        <FormGroup>

        <label>Last Name:</label>

        <input 
          name='lname' 
          type='text'
          value={lname}
         // required
          onChange={e => setlName(e.target.value)}
        /></FormGroup>
        </col>

         <FormGroup>
        <label>Date Of Birth:</label>

        <input
          name='date' 
          type='date'
          value={date}
        //  required
          onChange={e => setdate(e.target.value)}
        /></FormGroup>
        <FormGroup>
        <label>Email:</label>

        <input
          name='email' 
          type='email'
          value={email}
          //required
          onChange={e => setemail(e.target.value)}
        /></FormGroup>
         <FormGroup>
        <label>Password:</label>

        <input
          name='password' 
          type='password'
          value={pswd}
          //required
          onChange={e => setpswd(e.target.value)}

        /></FormGroup>
         <FormGroup>
        <label>Social Media Account:</label>

        <input 
          name='SoMdAc' 
          type='text' 
          value={SoMdAc}
          onChange={e => setSoMdAc(e.target.value)}
        /></FormGroup>
          <FormGroup>
        <label>Upload your Profile:</label>

        <input 
          name='img' 
          type='file' 
          value={img}
          onChange={e => setimg(e.target.value)}
        /></FormGroup>


        <input 
          type='submit' 
          value='Add User' 
        />
      </form>
      </div>
    )
  }

  export default AddUser;
