import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default class User extends Component {

    constructor(props) {
        super(props);
        this.onChangeUserFName = this.onChangeUserFName.bind(this);
        this.onChangeUserProfileImg = this.onChangeUserProfileImg.bind(this);
     //   this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            profileImg: '',
            fname:''
        }
    }
     onChangeUserFName(e) {
        this.setState({ fname: e.target.value })
      }
      onChangeUserProfileImg(e) {
        this.setState({ profileImg: e.target.files[0] })
    }

    onSubmit(e) {
        const userObject = {
            fname: this.state.fname,
            profileImg :this.profileImg
          };

        console.log("knxdj")
        e.preventDefault()
        // const formData = new FormData()
        // formData.append('profileImg', this.state.profileImg)
        // formData.append('fname', this.state.fname)
        axios.post("http://localhost:4000/users/add-user", userObject, {
        }).then(res => {
            console.log(res)
            this.setState({ fname: '', profileImg: ''})
        })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <form onSubmit={this.onSubmit}encType="multipart/form-data">
                    <Form.Group  controlId="FName">
                    <Form.Label>First Name</Form.Label>
                     <Form.Control type="text" value={this.state.fname} onChange={this.onChangeUserFName } />
                   </Form.Group>
                   <Form.Group controlId="ProfileImg">
                   <Form.Label>First Name</Form.Label>
                   <Form.Control  type="file"value={this.state.profileImg} onChange={this.onChangeUserProfileImg} />
                   </Form.Group>
                   <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
         Add User
        </Button>
                    </form>
                </div>
            </div>
        )
    }
}
