import React, { Component } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class AddMyblog extends Component {

  constructor(props) {
    super(props)

    // Setting up functions
    this.onChangeUserName = this.onChangeUserName.bind(this);
    this.onChangeUserBlog= this.onChangeUserBlog.bind(this);


    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      name: '',
      blog:'',


    }
  }

  onChangeUserName(e) {
    this.setState({ name: e.target.value })
  }
  onChangeUserBlog(e) {
    this.setState({ blog: e.target.value })
  }








  onSubmit(e) {
    e.preventDefault()

    const blogObject = {
      name: this.state.name,
      blog: this.state.blog,

    };


    axios.post('http://localhost:4000/blogs/blog', blogObject)
     .then(res => console.log(res.data));


    this.setState({ name: '', blog:'' }) 
  }

  render() {
    return (
    <div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
      <Form.Group controlId="UserName">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" value={this.state.name} onChange={this.onChangeUserName} />
        </Form.Group>
        <Form.Group controlId="UserBlog" id="userblog">
          <Form.Label>Blog</Form.Label>
          <Form.Control type="textarea" value={this.state.blog} onChange={this.onChangeUserBlog} />
        </Form.Group>




        <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
         Add User
        </Button>
      </Form>
    </div>);
  }
}
