import React, { Component } from "react";
import axios from 'axios';
import Table from 'react-bootstrap/Table';


export default class Showblog extends Component {

  constructor(props) {
    super(props)
    this.state = {
      blogs: []
    };
  }



  componentDidMount() {
    axios.get('http://localhost:4000/users/')
      .then(res => {
        this.setState({
            blogs: res.data
        });
      })
      .catch((error) => {
        console.log(error);
      })
  }




  render() {
    return (
    <div className="table-wrapper-showuser">
      <Table striped bordered hover>
        <thead>
          <tr>
            <th> Name</th>
            <th>Blog</th>



          </tr>
        </thead>
        <tbody>

        {blogs.map((blog)=>
                <tr>
                <td>{blog.name}</td>
                <td>{blog.blog}</td>



                </tr>
                )} 
        </tbody>
      </Table>
    </div>);
  }
}

