import React, { Component } from 'react';

import {Form ,Button} from 'react-bootstrap/Form';


import { useState } from 'react';
import { FormGroup } from 'reactstrap';

  function AddUser({ addChoreLog }) {

    const [fname, setfName] = useState();
    const [lname, setlName] = useState();
    const [email, setemail] = useState();
    const [pswd, setpswd] = useState();
    const [SoMdAc,setSoMdAc] = useState();
    const [img, setimg] = useState();

    const [date, setDate] = useState();
    const handleSubmit= (e) => {
        AddUser([fname,lname,email,pswd,setSoMdAc,date])
      e.preventDefault();
    }

    return (
      <form onSubmit={e => { handleSubmit(e) }}>
          <FormGroup>
        <label>Add User :</label>

        <input 
          name='fname' 
          type='text'
          value={fname}
         // required
          onChange={e => setfName(e.target.value)}
        /></FormGroup>
        <br/>
        <FormGroup>
        <label>Name:</label>

        <input 
          name='lname' 
          type='text' 
          value={lname}
          //required
          onChange={e => setlName(e.target.value)}
        /></FormGroup>
         <FormGroup>
        <label>Date:</label>

        <input
          name='date' 
          type='date'
          value={date}
        //  required
          onChange={e => setDate(e.target.value)}
        /></FormGroup>
        <FormGroup>
        <label>Email:</label>

        <input
          name='email' 
          type='email'
          value={email}
          //required
          onChange={e => setDate(e.target.value)}
        /></FormGroup>
         <FormGroup>
        <label>Password:</label>

        <input
          name='Password' 
          type='password'
          value={pswd}
          //required
          onChange={e => setDate(e.target.value)}

        /></FormGroup>
         <FormGroup>
        <label>Social Media Account:</label>

        <input 
          name='SoMdAc' 
          type='text' 
          value={SoMdAc}
          onChange={e => setlName(e.target.value)}
        /></FormGroup>
          <FormGroup>
        <label>Upload your Profile:</label>

        <input 
          name='img' 
          type='file' 
          value={img}
          onChange={e => setlName(e.target.value)}
        /></FormGroup>
        <input type="submit" value="Go to my link location" 
    onclick="window.location='/my/link/location';" /> 
        <input 
          type='submit' 
          value='Add User' 
        />
      </form>
    )
  }

  export default AddUser;
