import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default class User extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeUserFName = this.onChangeUserFName.bind(this);
        this.state = {
            profileImg: '',
            fname:''
        }
    }
    onChangeUserFName(e) {
        this.setState({ fname: e.target.value })
      }
    onFileChange(e) {
        this.setState({ profileImg: e.target.files[0] })
    }

    onSubmit(e) {
        console.log("knxdj")
        e.preventDefault()
        const userObject = {
            fname: this.state.fname,
            profileImg:this.state.profileImg
          };
        // const formData = new FormData()
        // formData.append('profileImg', this.state.profileImg)
        // formData.append('fname', this.state.fname)
        // axios.post("http://localhost:4000/users/add-user", formData, {
        // }).then(res => {
        //     console.log(res)
        //     this.setState({ fname: '', profileImg: ''})
        // })
        axios.post('http://localhost:4000/users/add-user', userObject)
        .then(res => console.log(res.data));


       this.setState({ fname: '', profileImg:'' })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <form onSubmit={this.onSubmit}encType="multipart/form-data">
                    <Form.Group >
                    <Form.Label>First Name</Form.Label>
                     <Form.Control type="text"  onChange={this.onChangeUserFName } />
                   </Form.Group>
                   <Form.Group>
                   <Form.Label>First Name</Form.Label>
                   <Form.Control  type="file" onChange={this.onFileChange} />
                   </Form.Group>
                   <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
         Add User
        </Button>
                    </form>
                </div>
            </div>
        )
    }
}
