import React, { Component } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class AddMyblog extends Component {

  constructor(props) {
    super(props)

    // Setting up functions
    this.onChangeUserName = this.onChangeUserName.bind(this);
    this.onChangeUserBlog= this.onChangeUserBlog.bind(this);


    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      name: '',
      blog:'',


    }
  }

  onChangeUserName(e) {
    this.setState({ name: e.target.value })
  }
  onChangeUserBlog(e) {
    this.setState({ blog: e.target.value })
  }








  onSubmit(e) {
    e.preventDefault()

    const userObject = {
      name: this.state.fname,
      blog: this.state.lname,

    };


    axios.post('http://localhost:4000/users/add-user', userObject)
     .then(res => console.log(res.data));


    this.setState({ fname: '', lname:'' })
  }

  render() {
    return (
    <div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="FName">
          <Form.Label>First Name</Form.Label>
          <Form.Control type="text" value={this.state.fname} onChange={this.onChangeUserFName } />
        </Form.Group>
        <Form.Group controlId="LName">
          <Form.Label>Last Name</Form.Label>
          <Form.Control type="text" value={this.state.lname} onChange={this.onChangeUserLName} />
        </Form.Group>




        <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
         Add User
        </Button>
      </Form>
    </div>);
  }
}
