import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default class User extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.onChangeUserFName = this.onChangeUserFName.bind(this);
        this.onChangeUserLName = this.onChangeUserLName.bind(this);
        this.onChangeUserDob =   this.onChangeUserDob.bind(this);
        this.onChangeUserEmail = this.onChangeUserEmail.bind(this);
        this.onChangeUserPassword = this.onChangeUserPassword.bind(this);

        this.state = {

            fname:'',
            lname:'',
             dob:'',

             email: '',
             password:'',
             profileImg: '',
        }
    }
    onChangeUserFName(e) {
        this.setState({ fname: e.target.value })
      }
      onChangeUserLName(e) {
        this.setState({ lname: e.target.value })
      }
      onChangeUserDob(e) {
        this.setState({ dob: e.target.value })
      }
      onChangeUserEmail(e) {
        this.setState({ email: e.target.value })
      }
      onChangeUserPassword(e) {
        this.setState({ password: e.target.value })
      }
    onFileChange(e) {
        this.setState({ profileImg: e.target.files[0] })
    }

    onSubmit(e) {
        console.log("knxdj")
        // const userObject = {
        //     fname: this.state.fname,
        //     lname: this.state.lname,
        //     email: this.state.email,
        //     dob:this.state.dob,
        //     password:this.state.password,
        //     profileImg:this.state.profileImg
        //   };

        e.preventDefault()
        // const userObject = {
        //     fname: this.state.fname,
        //     profileImg:this.state.profileImg
        //   };
        const formData = new FormData()

        formData.append('fname', this.state.fname)
        formData.append('lname', this.state.lname)
        formData.append('dob', this.state.dob)
        formData.append('email', this.state.email)
        formData.append('password', this.state.password)
        formData.append('profileImg', this.state.profileImg)
        axios.post("http://localhost:4000/users/add-user", formData, {
        }).then(res => {
            console.log(res)
           // this.setState({ fname: '', profileImg: ''})
        })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <form onSubmit={this.onSubmit}encType="multipart/form-data">
                    <Form.Group >
                    <Form.Label>First Name</Form.Label>
                     <Form.Control type="text"  onChange={this.onChangeUserFName } />
                   </Form.Group>
                   <Form.Group >
                    <Form.Label>Last Name</Form.Label>
                     <Form.Control type="text"  onChange={this.onChangeUserLName } />
                   </Form.Group>
                   <Form.Group >
                    <Form.Label>Email</Form.Label>
                     <Form.Control  type="email" onChange={this.onChangeUserEmail } />
                   </Form.Group>
                   <Form.Group >
                    <Form.Label>Password</Form.Label>
                     <Form.Control type="password"  onChange={this.onChangeUserPassword } />
                   </Form.Group>
                   <Form.Group>
                   <Form.Group controlId="DOB">
          <Form.Label>Date of Birth</Form.Label>
          <Form.Control type="date"  onChange={this.onChangeUserDob} />
        </Form.Group>
                   <Form.Label>First Name</Form.Label>
                   <Form.Control  type="file" onChange={this.onFileChange} />
                   </Form.Group>
                   <Button variant="danger" size="lg" block="block" type="submit" className="mt-4">
         Add User
        </Button>
                    </form>
                </div>
            </div>
        )
    }
}
