import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

export default class User extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeUserFName = this.onChangeUserFName.bind(this);
        this.state = {
            profileImg: '',
            fname:''
        }
    }
    onChangeUserFName(e) {
        this.setState({ fname: e.target.value })
      }
    onFileChange(e) {
        this.setState({ profileImg: e.target.files[0] })
    }

    onSubmit(e) {
        console.log("knxdj")
        e.preventDefault()
        const formData = new FormData()
        formData.append('profileImg', this.state.profileImg)
        formData.append('fname', this.state.fname)
        axios.post("http://localhost:4000/users/add-user", formData, {
        }).then(res => {
            console.log(res)
            this.setState({ fname: '', profileImg: ''})
        })
    }


    render() {
        return (
            <div className="container">
                <div className="row">
                    <form onSubmit={this.onSubmit}encType="multipart/form-data" method="post">
                    <Form.Group controlId="FName">
                    <Form.Label>First Name</Form.Label>
                     <Form.Control type="text" value={this.state.fname} onChange={this.onChangeUserFName } required />
                   </Form.Group>
                        <div className="form-group">
                            <input type="file" onChange={this.onFileChange} />
                        </div>
                        <div className="form-group">
                            <button className="btn btn-primary"   type="submit" >Upload</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
